","keccak256":"0xeda2c624a3bdb6ba7971d10fedd09029a7dd5b2f5297f1ad09f4b10831bc6cb3","license":"MIT"},"contracts/legacy/PallasCurveLegacy.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../PallasTypes.sol";

/**
 * @title PallasCurve
 * @dev Implementation of Pallas curve operations
 */
contract PallasCurveLegacy is PallasTypes {
    /// @notice Field modulus for Pallas curve
    uint256 public constant FIELD_MODULUS =
        0x40000000000000000000000000000000224698fc094cf91b992d30ed00000001;

    /// @notice Scalar field modulus for Pallas curve
    uint256 public constant SCALAR_MODULUS =
        0x40000000000000000000000000224698fc0994a8dd8c46eb2100000001;

    /// @notice Curve equation constant (B) where y² = x³ + B
    uint256 public constant BEQ = 5;

    /// @notice Default signature prefix for testnet
    string public constant SIGNATURE_PREFIX = "CodaSignature*******";

    /// @notice Signature prefix for mainnet
    string public constant MAINNET_SIGNATURE_PREFIX = "MinaSignatureMainnet";

    /// @notice Default length for string operations
    uint256 public constant DEFAULT_STRING_LENGTH = 128;

    /// @notice Generator point x-coordinate
    uint256 public constant G_X = 1;

    /// @notice Generator point y-coordinate
    uint256 public constant G_Y =
        0x1b74b5a30a12937c53dfa9f06378ee548f655bd4333d477119cf7a23caed2abb;

    /// @notice Performs modular addition
    /// @dev Wrapper around Solidity's addmod
    /// @param x First operand
    /// @param y Second operand
    /// @param k Modulus
    /// @return uint256 Result of (x + y) mod k
    function add(uint x, uint y, uint k) internal pure returns (uint256) {
        return addmod(x, y, k);
    }

    /// @notice Performs modular multiplication
    /// @dev Wrapper around Solidity's mulmod
    /// @param x First operand
    /// @param y Second operand
    /// @param k Modulus
    /// @return uint256 Result of (x * y) mod k
    function mul(uint x, uint y, uint k) internal pure returns (uint256) {
        return mulmod(x, y, k);
    }

    /// @notice Computes modular multiplicative inverse
    /// @dev Implements extended Euclidean algorithm
    /// @param a Value to invert
    /// @return uint256 Modular multiplicative inverse of a
    function invmod(uint256 a) internal pure returns (uint256) {
        if (a == 0) revert("Cannot invert 0");

        uint256 t = 0;
        uint256 newt = 1;
        uint256 r = FIELD_MODULUS;
        uint256 newr = a;
        uint256 quotient;
        uint256 temp;

        unchecked {
            while (newr != 0) {
                quotient = r / newr;

                temp = t;
                t = newt;
                newt = temp - quotient * newt;

                temp = r;
                r = newr;
                newr = temp - quotient * newr;
            }

            if (t > FIELD_MODULUS) {
                t += FIELD_MODULUS;
            }
        }
        return t;
    }

    /// @notice Performs modular exponentiation
    /// @dev Uses precompiled contract at address 0x05
    /// @param base Base value
    /// @param exponent Exponent value
    /// @param modulus Modulus for operation
    /// @return result Result of base^exponent mod modulus
    function modExp(
        uint256 base,
        uint256 exponent,
        uint256 modulus
    ) internal view returns (uint256 result) {
        assembly {
            // Free memory pointer
            let p := mload(0x40)

            // Store length of base, exponent and modulus
            mstore(p, 0x20)
            mstore(add(p, 0x20), 0x20)
            mstore(add(p, 0x40), 0x20)

            // Store base, exponent and modulus
            mstore(add(p, 0x60), base)
            mstore(add(p, 0x80), exponent)
            mstore(add(p, 0xa0), modulus)

            // Call precompiled contract for modular exponentiation
            if iszero(staticcall(gas(), 0x05, p, 0xc0, p, 0x20)) {
                revert(0, 0)
            }

            result := mload(p)
        }
    }

    /// @notice Computes modular square root using Tonelli-Shanks algorithm
    /// @dev Implements for prime modulus
    /// @param n Value to find square root of
    /// @param p Modulus (must be prime)
    /// @return uint256 Modular square root of n
    function sqrtmod(uint256 n, uint256 p) internal view returns (uint256) {
        if (n == 0) return 0;

        // Calculate Q and M where p - 1 = Q * 2^M and Q is odd
        uint256 Q = p - 1;
        uint256 M = 0;
        while (Q % 2 == 0) {
            Q /= 2;
            M++;
        }

        // Find a non-residue z
        uint256 z = 2;
        while (true) {
            if (modExp(z, (p - 1) / 2, p) == p - 1) break; // Found a non-residue
            z++;
        }

        uint256 c = modExp(z, Q, p);
        uint256 t = modExp(n, Q >> 1, p); // n^((Q-1)/2)
        uint256 R = mulmod(t, n, p); // n^((Q+1)/2)
        t = mulmod(t, R, p); // n^Q

        while (t != 1) {
            uint256 i = 0;
            uint256 s = t;
            while (s != 1 && i < M) {
                s = mulmod(s, s, p);
                i++;
            }
            require(i < M, "Square root does not exist");

            uint256 b = c;
            for (uint256 j = 0; j < M - i - 1; j++) {
                b = mulmod(b, b, p);
            }
            M = i;
            c = mulmod(b, b, p);
            t = mulmod(t, c, p);
            R = mulmod(R, b, p);
        }

        return R;
    }

    /// @notice Checks if a number is even
    /// @dev Uses bitwise AND operation
    /// @param x Number to check
    /// @return bool True if number is even
    function isEven(uint256 x) internal pure returns (bool) {
        return (x & 1) == 0;
    }

    /// @notice Verifies if a point lies on the Pallas curve
    /// @dev Checks if point satisfies y² = x³ + 5
    /// @param p Point to check
    /// @return bool True if point is on curve
    function isOnCurve(Point memory p) internal pure returns (bool) {
        if (p.x >= FIELD_MODULUS || p.y >= FIELD_MODULUS) {
            return false;
        }

        uint256 lhs = mulmod(p.y, p.y, FIELD_MODULUS);
        uint256 x2 = mulmod(p.x, p.x, FIELD_MODULUS);
        uint256 x3 = mulmod(x2, p.x, FIELD_MODULUS);
        uint256 rhs = addmod(x3, BEQ, FIELD_MODULUS);

        return lhs == rhs;
    }

    /// @notice Converts a point from affine to projective coordinates
    /// @dev Returns (1:1:0) for point at infinity
    /// @param p Point in affine coordinates
    /// @return ProjectivePoint Point in projective coordinates
    function toProjective(
        Point memory p
    ) internal pure returns (ProjectivePoint memory) {
        if (p.x == 0 && p.y == 0) {
            return ProjectivePoint(1, 1, 0); // Point at infinity
        }
        return ProjectivePoint(p.x, p.y, 1);
    }

    /// @notice Converts a point from projective to affine coordinates
    /// @dev Handles point at infinity case
    /// @param p Point in projective coordinates
    /// @return Point Point in affine coordinates
    function toAffine(
        ProjectivePoint memory p
    ) internal pure returns (Point memory) {
        if (p.z == 0) {
            return Point(0, 0); // Point at infinity
        }

        uint256 zinv = invmod(p.z);
        uint256 zinv_squared = mulmod(zinv, zinv, FIELD_MODULUS);

        return
            Point(
                mulmod(p.x, zinv_squared, FIELD_MODULUS),
                mulmod(
                    p.y,
                    mulmod(zinv, zinv_squared, FIELD_MODULUS),
                    FIELD_MODULUS
                )
            );
    }

    /// @notice Doubles a point in projective coordinates
    /// @dev Specialized operation for when both points are the same
    /// @param g Point to double
    /// @return ProjectivePoint Doubled point
    function projectiveDouble(
        ProjectivePoint memory g
    ) internal pure returns (ProjectivePoint memory) {
        if (g.z == 0) return g;
        if (g.y == 0) revert("Cannot double point with y=0");

        unchecked {
            // Cache x, y, z values
            uint256 x = g.x;
            uint256 y = g.y;
            uint256 z = g.z;

            // A = X1^2
            uint256 A = mulmod(x, x, FIELD_MODULUS);
            // B = Y1^2
            uint256 B = mulmod(y, y, FIELD_MODULUS);
            // C = B^2
            uint256 C = mulmod(B, B, FIELD_MODULUS);

            // Cache X1+B
            uint256 xPlusB = addmod(x, B, FIELD_MODULUS);
            // D = 2*((X1+B)^2-A-C)
            uint256 D = mulmod(
                2,
                addmod(
                    mulmod(xPlusB, xPlusB, FIELD_MODULUS),
                    FIELD_MODULUS - addmod(A, C, FIELD_MODULUS),
                    FIELD_MODULUS
                ),
                FIELD_MODULUS
            );

            // E = 3*A
            uint256 E = mulmod(3, A, FIELD_MODULUS);
            // F = E^2
            uint256 F = mulmod(E, E, FIELD_MODULUS);

            // Calculate X3 first as it's needed for Y3
            uint256 X3 = addmod(
                F,
                FIELD_MODULUS - mulmod(2, D, FIELD_MODULUS),
                FIELD_MODULUS
            );

            return
                ProjectivePoint(
                    X3,
                    addmod(
                        mulmod(
                            E,
                            addmod(D, FIELD_MODULUS - X3, FIELD_MODULUS),
                            FIELD_MODULUS
                        ),
                        FIELD_MODULUS - mulmod(8, C, FIELD_MODULUS),
                        FIELD_MODULUS
                    ),
                    mulmod(2, mulmod(y, z, FIELD_MODULUS), FIELD_MODULUS)
                );
        }
    }

    /// @notice Adds two points in projective coordinates
    /// @dev Complete addition formula for Pallas curve
    /// @param g First point
    /// @param h Second point
    /// @return ProjectivePoint Resulting point
    function projectiveAdd(
        ProjectivePoint memory g,
        ProjectivePoint memory h
    ) internal pure returns (ProjectivePoint memory) {
        if (g.z == 0) return h;
        if (h.z == 0) return g;

        unchecked {
            uint256 z1 = g.z;
            uint256 z2 = h.z;

            uint256 Z1Z1 = mulmod(z1, z1, FIELD_MODULUS);
            uint256 Z2Z2 = mulmod(z2, z2, FIELD_MODULUS);
            uint256 U1 = mulmod(g.x, Z2Z2, FIELD_MODULUS);
            uint256 U2 = mulmod(h.x, Z1Z1, FIELD_MODULUS);
            uint256 S1 = mulmod(
                g.y,
                mulmod(z2, Z2Z2, FIELD_MODULUS),
                FIELD_MODULUS
            );
            uint256 S2 = mulmod(
                h.y,
                mulmod(z1, Z1Z1, FIELD_MODULUS),
                FIELD_MODULUS
            );
            uint256 H = addmod(U2, FIELD_MODULUS - U1, FIELD_MODULUS);

            if (H == 0) {
                if (S1 == S2) {
                    return projectiveDouble(g);
                }
                if (addmod(S1, S2, FIELD_MODULUS) == 0) {
                    return ProjectivePoint(1, 1, 0);
                }
                revert("Invalid point addition");
            }

            uint256 I = mulmod(mulmod(H, H, FIELD_MODULUS), 4, FIELD_MODULUS);
            uint256 J = mulmod(H, I, FIELD_MODULUS);
            uint256 r = mulmod(
                2,
                addmod(S2, FIELD_MODULUS - S1, FIELD_MODULUS),
                FIELD_MODULUS
            );
            uint256 V = mulmod(U1, I, FIELD_MODULUS);

            // Calculate X3 first as it's needed for Y3
            uint256 X3 = addmod(
                mulmod(r, r, FIELD_MODULUS),
                FIELD_MODULUS -
                    addmod(J, mulmod(2, V, FIELD_MODULUS), FIELD_MODULUS),
                FIELD_MODULUS
            );

            return
                ProjectivePoint(
                    X3,
                    addmod(
                        mulmod(
                            r,
                            addmod(V, FIELD_MODULUS - X3, FIELD_MODULUS),
                            FIELD_MODULUS
                        ),
                        FIELD_MODULUS -
                            mulmod(
                                2,
                                mulmod(S1, J, FIELD_MODULUS),
                                FIELD_MODULUS
                            ),
                        FIELD_MODULUS
                    ),
                    mulmod(
                        addmod(
                            mulmod(
                                addmod(z1, z2, FIELD_MODULUS),
                                addmod(z1, z2, FIELD_MODULUS),
                                FIELD_MODULUS
                            ),
                            FIELD_MODULUS - addmod(Z1Z1, Z2Z2, FIELD_MODULUS),
                            FIELD_MODULUS
                        ),
                        H,
                        FIELD_MODULUS
                    )
                );
        }
    }

    /// @notice Adds two points in affine coordinates
    /// @dev Converts to projective, adds, then converts back
    /// @param p1 First point
    /// @param p2 Second point
    /// @return Point Sum of the points
    function addPoints(
        Point memory p1,
        Point memory p2
    ) internal pure returns (Point memory) {
        ProjectivePoint memory g = toProjective(p1);
        ProjectivePoint memory h = toProjective(p2);
        ProjectivePoint memory r = projectiveAdd(g, h);
        return toAffine(r);
    }

    /// @notice Performs scalar multiplication
    /// @dev Uses double-and-add algorithm
    /// @param p Base point
    /// @param scalar Scalar value
    /// @return Point Resulting point
    function scalarMul(
        Point memory p,
        uint256 scalar
    ) internal pure returns (Point memory) {
        ProjectivePoint memory g = toProjective(p);
        ProjectivePoint memory result = ProjectivePoint(1, 1, 0);
        ProjectivePoint memory current = g;

        // scalar = scalar % SCALAR_MODULUS;
        while (scalar > 0) {
            if (scalar & 1 == 1) {
                result = projectiveAdd(result, current);
            }
            current = projectiveDouble(current);
            scalar >>= 1;
        }

        return toAffine(result);
    }
}
","keccak256":"0x9c50c228c6a0d1555da893c2d2996743d03d5456167157c9c0b162dc40c5b753","license":"MIT"},"contracts/legacy/PoseidonLegacy.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./PallasConstantsLegacy.sol";
import "./PallasCurveLegacy.sol";

/**
 * @title PoseidonLegacy
 * @dev Implementation of Poseidon hash function for t = 3 (2 inputs)
 */

contract PoseidonLegacy is PallasCurveLegacy, PallasConstantsLegacy {
    uint256 internal constant BITS_PER_FIELD = 254;
    uint256 internal constant CODA_PREFIX_FIELD =
        240717916736854602989207148466022993262069182275;
    uint256 internal constant MINA_PREFIX_FIELD =
        664504924603203994814403132056773144791042910541;

    struct HashInputLegacy {
        uint256[] fields;
        bool[] bits;
    }

    /// @notice Computes x^5 mod FIELD_MODULUS
    /// @dev Optimized power5 implementation using square-and-multiply
    /// @param x Base value
    /// @return uint256 Result of x^5
    function power5(uint256 x) internal pure returns (uint256) {
        uint256 x2 = mulmod(x, x, FIELD_MODULUS);
        uint256 x4 = mulmod(x2, x2, FIELD_MODULUS);
        return mulmod(x4, x, FIELD_MODULUS);
    }

    /// @notice Initial state array [0, 0, 0]
    /// @dev Creates starting state for Poseidon hash
    /// @return uint256[3] Initial state array
    function initialState() internal pure returns (uint256[3] memory) {
        return [uint256(0), uint256(0), uint256(0)];
    }

    /// @notice Retrieves value from MDS matrix at specified position
    /// @dev Used in the Poseidon permutation
    /// @param row Row index
    /// @param col Column index
    /// @return uint256 Matrix value at position
    function getMdsValue(
        uint256 row,
        uint256 col
    ) internal view returns (uint256) {
        require(row < 3 && col < 3, "Invalid MDS indices");
        return mdsMatrix[row][col];
    }

    /// @notice Gets round constant for specified round and position
    /// @dev Access round constants array with bounds checking
    /// @param round Round number
    /// @param pos Position in the round
    /// @return uint256 Round constant value
    function getRoundConstant(
        uint256 round,
        uint256 pos
    ) internal view returns (uint256) {
        require(
            round < (POSEIDON_FULL_ROUNDS + 1) && pos < 3, // +1 for initial round constant
            "Invalid round constant indices"
        );
        return roundConstants[round][pos];
    }

    /// @notice Performs matrix multiplication with MDS matrix
    /// @dev Core operation in Poseidon permutation
    /// @param state Current state array
    /// @return result Result of matrix multiplication
    function mdsMultiply(
        uint256[3] memory state
    ) internal view returns (uint256[3] memory result) {
        // Unroll the loops for gas efficiency
        result[0] = addmod(
            addmod(
                mulmod(getMdsValue(0, 0), state[0], FIELD_MODULUS),
                mulmod(getMdsValue(0, 1), state[1], FIELD_MODULUS),
                FIELD_MODULUS
            ),
            mulmod(getMdsValue(0, 2), state[2], FIELD_MODULUS),
            FIELD_MODULUS
        );

        result[1] = addmod(
            addmod(
                mulmod(getMdsValue(1, 0), state[0], FIELD_MODULUS),
                mulmod(getMdsValue(1, 1), state[1], FIELD_MODULUS),
                FIELD_MODULUS
            ),
            mulmod(getMdsValue(1, 2), state[2], FIELD_MODULUS),
            FIELD_MODULUS
        );

        result[2] = addmod(
            addmod(
                mulmod(getMdsValue(2, 0), state[0], FIELD_MODULUS),
                mulmod(getMdsValue(2, 1), state[1], FIELD_MODULUS),
                FIELD_MODULUS
            ),
            mulmod(getMdsValue(2, 2), state[2], FIELD_MODULUS),
            FIELD_MODULUS
        );
    }

    /// @notice Performs the Poseidon permutation
    /// @dev Main cryptographic operation including rounds of substitution and diffusion
    /// @param state Input state array
    /// @return uint256[3] Permuted state
    function poseidonPermutation(
        uint256[3] memory state
    ) internal view returns (uint256[3] memory) {
        uint256 offset = 0;

        if (POSEIDON_HAS_INITIAL_ROUND_CONSTANT) {
            state[0] = addmod(state[0], roundConstants[0][0], FIELD_MODULUS);
            state[1] = addmod(state[1], roundConstants[0][1], FIELD_MODULUS);
            state[2] = addmod(state[2], roundConstants[0][2], FIELD_MODULUS);
            offset = 1;
        }

        for (uint256 round = 0; round < POSEIDON_FULL_ROUNDS; round++) {
            state[0] = power5(state[0]);
            state[1] = power5(state[1]);
            state[2] = power5(state[2]);

            state = mdsMultiply(state);

            state[0] = addmod(
                state[0],
                getRoundConstant(round + offset, 0),
                FIELD_MODULUS
            );
            state[1] = addmod(
                state[1],
                getRoundConstant(round + offset, 1),
                FIELD_MODULUS
            );
            state[2] = addmod(
                state[2],
                getRoundConstant(round + offset, 2),
                FIELD_MODULUS
            );
        }

        return state;
    }

    /// @notice Updates hash state with input
    /// @dev Processes input in rate-sized blocks
    /// @param state Current state
    /// @param input Input values
    /// @return uint256[3] Updated state
    function update(
        uint256[3] memory state,
        uint256[] memory input
    ) internal view returns (uint256[3] memory) {
        if (input.length == 0) {
            return poseidonPermutation(state);
        }

        uint256 n = ((input.length + POSEIDON_RATE - 1) / POSEIDON_RATE) *
            POSEIDON_RATE;
        uint256[] memory paddedInput = new uint256[](n);
        for (uint256 i = 0; i < input.length; i++) {
            paddedInput[i] = input[i];
        }

        for (
            uint256 blockIndex = 0;
            blockIndex < n;
            blockIndex += POSEIDON_RATE
        ) {
            state[0] = addmod(state[0], paddedInput[blockIndex], FIELD_MODULUS);
            if (blockIndex + 1 < n) {
                state[1] = addmod(
                    state[1],
                    paddedInput[blockIndex + 1],
                    FIELD_MODULUS
                );
            }
            state = poseidonPermutation(state);
        }
        return state;
    }

    /// @notice Converts string to array of bits
    /// @dev Matches o1js bit ordering
    /// @param str Input string
    /// @return bool[] Array of bits
    function stringToBits(
        string memory str
    ) internal pure returns (bool[] memory) {
        bytes memory strBytes = bytes(str);
        bool[] memory bits = new bool[](strBytes.length * 8);

        for (uint i = 0; i < strBytes.length; i++) {
            uint8 b = uint8(strBytes[i]);
            for (uint j = 0; j < 8; j++) {
                bits[i * 8 + j] = (b & (1 << (7 - j))) != 0;
            }
        }
        return bits;
    }

    /// @notice Converts bits to bytes
    /// @dev Used in field element conversion
    /// @param bits Array of bits
    /// @return bytes Resulting byte array
    function bitsToBytes(
        bool[] memory bits
    ) internal pure returns (bytes memory) {
        uint256 numBytes = (bits.length + 7) / 8;
        bytes memory result = new bytes(numBytes);

        for (uint256 i = 0; i < numBytes; i++) {
            uint8 byteVal = 0;
            for (uint8 bit = 0; bit < 8; bit++) {
                uint256 bitIndex = i * 8 + bit;
                if (bitIndex < bits.length && bits[bitIndex]) {
                    byteVal |= uint8(1 << bit);
                }
            }
            result[i] = bytes1(byteVal);
        }
        return result;
    }

    /// @notice Converts bytes to field element
    /// @dev Reduces result modulo FIELD_MODULUS
    /// @param b Input bytes
    /// @return uint256 Field element
    function bytesToFieldElement(
        bytes memory b
    ) internal pure returns (uint256) {
        uint256 result = 0;
        for (uint256 i = 0; i < b.length; i++) {
            result += uint256(uint8(b[i])) << (i * 8);
        }
        return result % FIELD_MODULUS;
    }

    /// @notice Converts prefix string to field element
    /// @dev Uses little-endian byte ordering
    /// @param prefix Input string
    /// @return uint256 Field element
    function prefixToField(
        string memory prefix
    ) internal pure returns (uint256) {
        bytes memory prefixBytes = bytes(prefix);
        require(prefixBytes.length < 32, "prefix too long");

        uint256 result = 0;
        // Process in little-endian order (like o1js)
        for (uint i = 0; i < 32; i++) {
            if (i < prefixBytes.length) {
                result |= uint256(uint8(prefixBytes[i])) << (i * 8);
            }
        }

        return result % FIELD_MODULUS;
    }

    /// @notice Packs HashInputLegacy into array of field elements
    /// @dev Handles both field and bit inputs
    /// @param input HashInputLegacy struct
    /// @return uint256[] Array of packed field elements
    function packToFieldsLegacy(
        HashInputLegacy memory input
    ) internal pure returns (uint256[] memory) {
        uint256 numBitFields = (input.bits.length + BITS_PER_FIELD - 1) /
            BITS_PER_FIELD;
        uint256[] memory result = new uint256[](
            input.fields.length + numBitFields
        );

        for (uint256 i = 0; i < input.fields.length; i++) {
            result[i] = input.fields[i];
        }

        uint256 bitsProcessed = 0;
        for (uint256 i = 0; i < numBitFields; i++) {
            uint256 bitsToTake = bitsProcessed + BITS_PER_FIELD >
                input.bits.length
                ? input.bits.length - bitsProcessed
                : BITS_PER_FIELD;

            bool[] memory fieldBits = new bool[](BITS_PER_FIELD);
            for (uint256 j = 0; j < bitsToTake; j++) {
                fieldBits[j] = input.bits[bitsProcessed + j];
            }

            bytes memory fieldBytes = bitsToBytes(fieldBits);
            result[input.fields.length + i] = bytesToFieldElement(fieldBytes);
            bitsProcessed += bitsToTake;
        }

        return result;
    }

    /// @notice Combines two HashInputLegacy structs
    /// @dev Concatenates both fields and bits arrays
    /// @param input1 First input
    /// @param input2 Second input
    /// @return HashInputLegacy Combined input
    function appendHashInputs(
        HashInputLegacy memory input1,
        HashInputLegacy memory input2
    ) internal pure returns (HashInputLegacy memory) {
        uint256[] memory combinedFields = new uint256[](
            input1.fields.length + input2.fields.length
        );
        bool[] memory combinedBits = new bool[](
            input1.bits.length + input2.bits.length
        );

        for (uint256 i = 0; i < input1.fields.length; i++) {
            combinedFields[i] = input1.fields[i];
        }
        for (uint256 i = 0; i < input2.fields.length; i++) {
            combinedFields[input1.fields.length + i] = input2.fields[i];
        }

        for (uint256 i = 0; i < input1.bits.length; i++) {
            combinedBits[i] = input1.bits[i];
        }
        for (uint256 i = 0; i < input2.bits.length; i++) {
            combinedBits[input1.bits.length + i] = input2.bits[i];
        }

        return HashInputLegacy({fields: combinedFields, bits: combinedBits});
    }

    // @notice Computes Poseidon hash of input array
    /// @dev Main hashing function without prefix
    /// @param input Array to hash
    /// @return uint256 Hash result
    function poseidonLegacyHash(
        uint256[] memory input
    ) public view returns (uint256) {
        uint256[3] memory state = initialState();
        state = update(state, input);

        return state[0];
    }

    /// @notice Computes Poseidon hash with prefix
    /// @dev Hashes prefix followed by input array
    /// @param prefix String prefix
    /// @param input Array to hash
    /// @return uint256 Hash result
    function poseidonLegacyHashWithPrefix(
        string memory prefix,
        uint256[] memory input
    ) public view returns (uint256) {
        // Start with initial state [0, 0, 0]
        uint256[3] memory state = initialState();

        // Create prefix array with single element
        uint256[] memory prefixArray = new uint256[](1);
        prefixArray[0] = prefixToField(prefix);

        // First update with prefix
        state = update(state, prefixArray);

        // Then update with input
        state = update(state, input);

        return state[0];
    }

    /// @notice Hashes message with public key and signature data
    /// @dev Complete message hashing matching o1js implementation
    /// @param message String message
    /// @param publicKey Public key point
    /// @param r Signature r value
    /// @param prefix Network prefix
    /// @return uint256 Hash result
    function hashMessageLegacy(
        string memory message,
        Point memory publicKey,
        uint256 r,
        string memory prefix
    ) internal view returns (uint256) {
        HashInputLegacy memory messageInput = HashInputLegacy({
            fields: new uint256[](0),
            bits: stringToBits(message)
        });

        uint256[] memory pkFields = new uint256[](3);
        pkFields[0] = publicKey.x;
        pkFields[1] = publicKey.y;
        pkFields[2] = r;

        HashInputLegacy memory pkInput = HashInputLegacy({
            fields: pkFields,
            bits: new bool[](0)
        });

        HashInputLegacy memory fullInput = appendHashInputs(
            messageInput,
            pkInput
        );
        uint256[] memory packedFields = packToFieldsLegacy(fullInput);

        // Use cached prefix values
        uint256 prefixField = keccak256(bytes(prefix)) ==
            keccak256(bytes("MinaSignatureMainnet"))
            ? MINA_PREFIX_FIELD
            : CODA_PREFIX_FIELD;

        uint256[] memory prefixArray = new uint256[](1);
        prefixArray[0] = prefixField;

        uint256[3] memory state = initialState();
        state = update(state, prefixArray);
        state = update(state, packedFields);

        return state[0];
    }
}
